import React, { useEffect, useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from "axios";
import "../addsubject/add.css";
import toast from 'react-hot-toast';

const Edit = () => {

 const subjects = {
    subjectname: "",
    sotc: ""
 }

 const {id} = useParams();
 const navigate = useNavigate();
 const [subject, setSubject] = useState(subjects);

 const inputChangeHandler = (e) =>{
    const {name, value} = e.target;
    setSubject({...subject, [name]:value});
    console.log(subject);
 }

 useEffect(()=>{
    axios.get(`http://localhost:8000/api/getone/${id}`)
    .then((response)=>{
        setSubject(response.data)
    })
    .catch((error)=>{
        console.log(error);
    })
 },[id])


 const submitForm = async(e)=>{
    e.preventDefault();
    await axios.put(`http://localhost:8000/api/update/${id}`, subject)
    .then((response)=>{
       toast.success(response.data.msg, {position:"top-right"})
       navigate("/")
    })
    .catch(error => console.log(error))
 }

  return (
    <div className='addSubject'>
        <Link to={"/"}>Quay lại</Link>
        <h3>Cập nhật môn học</h3>
        <form className='addSubjectForm' onSubmit={submitForm}>
            <div className="inputGroup">
                <label htmlFor="subjectname">Tên môn học</label>
                <input type="text" value={subject.subjectname} onChange={inputChangeHandler} id="subjectname" name="subjectname" autoComplete='off' placeholder='Tên môn học' />
            </div>
            <div className="inputGroup">
                <label htmlFor="sotc">Số tín chỉ</label>
                <input type="number" value={subject.sotc} onChange={inputChangeHandler} id="sotc" name="sotc" autoComplete='off' placeholder='Số tín chỉ' />
            </div>
            <div className="inputGroup">
                <button type="submit">Cập nhật</button>
            </div>
        </form>
    </div>
  )
}

export default Edit
