import React, { useEffect, useState } from 'react'
import axios from "axios";
import toast from "react-hot-toast";
import "./subject.css";
import { Link } from 'react-router-dom'

const Subject = () => {

  const [subjects, setSubjects] = useState([]);

  useEffect(()=>{

    const fetchData = async()=>{
        const response = await axios.get("http://localhost:8000/api/getall");
        setSubjects(response.data);
    }

    fetchData();

  },[])

  const deleteSubject = async(subjectId) =>{
      await axios.delete(`http://localhost:8000/api/delete/${subjectId}`)
      .then((respones)=>{
        setSubjects((prevUser)=> prevUser.filter((user)=> user._id !== subjectId))
        toast.success(respones.data.msg, {position: 'top-right'})
      })
      .catch((error) =>{
        console.log(error);
      })
  }

  return (
    <div className='subjectTable'>
        <h1 style={{paddingBottom: "20px"}}>Quản Lý Thông Tin Môn Học</h1>
        <Link to={"/add"} className='addButton'>Thêm môn học</Link>
        <table border={1} cellPadding={10} cellSpacing={0}>
            <thead>
                <tr>
                    <th>Mã Môn Học</th>
                    <th>Tên Môn Học</th>
                    <th>Số tín chỉ</th>
                    <th>Thao Tác</th>
                </tr>
            </thead>
            <tbody>
                {
                    subjects.map((subject, index)=>{
                        return(
                        <tr key={subject._id}>
                            <td>{index + 1}</td>
                            <td>{subject.subjectname}</td>
                            <td>{subject.sotc}</td>
                            <td className='actionButtons'>
                                <button onClick={()=> deleteSubject(subject._id)}><i className="fa-solid fa-trash"></i></button>
                                <Link to={`/edit/`+subject._id}><i className="fa-solid fa-pen-to-square"></i></Link>
                            </td>
                        </tr>
                        )
                    })
                }
                
            </tbody>
        </table>
    </div>
  )
}

export default Subject
